//Creates some menu buttons to replicate the default menu items
//so toolbars can be used in place of the menubar
tinymce.PluginManager.add('rollups', function(editor, url) {
	editor.addButton('myEdit', {
		type: 'menubutton',
		text: 'Edit',
		icon: false,
		menu: [editor.menuItems['undo'],
			editor.menuItems['Redo'],
			{text: '-'},
			editor.menuItems['cut'],
			editor.menuItems['copy'],
			editor.menuItems['paste'],
			editor.menuItems['pastetext'],					,
			{text: '-'},
			editor.menuItems['selectall'],
			{text: '-'},
			editor.menuItems['searchreplace']
		]
	});
	editor.addButton('myInsert', {
		type: 'menubutton',
		text: 'Insert',
		icon: false,
		menu: [editor.menuItems['media'],
			editor.menuItems['link'],
			editor.menuItems['image'],
			{text: '-'},
			editor.menuItems['charmap'],
			editor.menuItems['anchor']]
	});
	var button = editor.buttons['styleselect'];
	if (button) {
		button.menu.items.push(editor.menuItems['removeformat']);
	}
});
